const inputData = $input.item.json;
const body = inputData.body || inputData;

const formResponses = body.form_responses || [];
const schoolName = formResponses.find(q => q.question_number === 1)?.response || "Unknown School";
const fees = formResponses.find(q => q.question_number === 2)?.response_label || "Not provided";
const travelTime = formResponses.find(q => q.question_number === 3)?.response || "Not provided";
const activities = formResponses.find(q => q.question_number === 4)?.selected_activities || [];
const hasAC = formResponses.find(q => q.question_number === 5)?.response || "No";
const classExcitement = formResponses.find(q => q.question_number === 6)?.response || 5;
const learningEnv = formResponses.find(q => q.question_number === 7)?.response || 5;

const categories = [
  { name: "Teaching Quality", our: body.our_score1 || 0, gcm: body.other_score1 || 0, difference: (body.our_score1 || 0) - (body.other_score1 || 0) },
  { name: "Facilities", our: body.our_score2 || 0, gcm: body.other_score2 || 0, difference: (body.our_score2 || 0) - (body.other_score2 || 0) },
  { name: "Activities", our: body.our_score3 || 0, gcm: body.other_score3 || 0, difference: (body.our_score3 || 0) - (body.other_score3 || 0) },
  { name: "Environment", our: body.our_score4 || 0, gcm: body.other_score4 || 0, difference: (body.our_score4 || 0) - (body.other_score4 || 0) },
  { name: "Student Support", our: body.our_score5 || 0, gcm: body.other_score5 || 0, difference: (body.our_score5 || 0) - (body.other_score5 || 0) }
];

const categoryText = categories.map(c => `${c.name}: ${c.our}% vs ${c.gcm}% (${c.difference >= 0 ? '+' : ''}${c.difference}%)`).join('\n');

const fullContext = `School Comparison Analysis:

Current School: ${schoolName}
Competitor: GCM Convent School

Score Breakdown:
${categoryText}

Overall: ${schoolName} ${body.overall_score_our || 0}% vs GCM ${body.overall_score_others || 0}%

Growth Potential:
- Happiness: +${body.happiness_difference || 0}%
- Ranking: #${body.ranking_difference || 0}
- Total Improvement: ${body.total_improvement_difference || 0}%

Family Context:
- Fees: ${fees}
- Travel: ${travelTime}
- Activities: ${activities.join(', ') || 'Limited'}
- AC: ${hasAC}
- Excitement: ${classExcitement}/10
- Environment: ${learningEnv}/10

Key: GCM excels in ALL dimensions.`;

return {
  timestamp: new Date().toISOString(),
  school_name: schoolName,
  comparison_school: body.others_name || "GCM Convent School",
  mobile_number: body.mobile_number,
  our_school_score: body.overall_score_our || 0,
  gcm_score: body.overall_score_others || 0,
  score_difference: (body.overall_score_our || 0) - (body.overall_score_others || 0),
  categories: categories,
  categories_text: categoryText,
  happiness_difference: body.happiness_difference || 0,
  ranking_improvement: body.ranking_difference || 0,
  total_improvement: body.total_improvement_difference || 0,
  fees: fees,
  travel_time: travelTime,
  activities: activities.join(", "),
  activities_count: activities.length,
  has_ac: hasAC,
  class_excitement: classExcitement,
  learning_environment: learningEnv,
  full_context: fullContext,
  form_responses: formResponses,
  retry_count: body.retry_count || 0,
  max_retries: 3
};

