const items = $input.all();
const originalData = $('Transform Data').first().json;

const aiInsights = {
  executive_summary: '',
  category_insights: [],
  growth_potential: '',
  personalized_recommendation: '',
  generation_method: 'ai',
  models_used: 0
};

items.forEach(item => {
  const taskType = item.json.task_type;
  const output = item.json.response || item.json.output || item.json.text || '';
  const success = item.json.ai_success;

  if (!output || !success) return;

  aiInsights.models_used++;

  switch(taskType) {
    case 'executive_summary':
      aiInsights.executive_summary = output.trim();
      break;
    case 'category_insights':
      const insights = output.split('\n')
        .filter(line => line.trim().length > 20)
        .map(line => line.trim())
        .filter(line => 
          line.match(/^[0-9\.\-\*â€¢]/) || 
          line.toLowerCase().includes('teaching') || 
          line.toLowerCase().includes('facilities') || 
          line.toLowerCase().includes('activities') || 
          line.toLowerCase().includes('environment') || 
          line.toLowerCase().includes('support')
        );
      aiInsights.category_insights = insights.slice(0, 5);
      break;
    case 'growth_potential':
      aiInsights.growth_potential = output.trim();
      break;
    case 'recommendation':
      aiInsights.personalized_recommendation = output.trim();
      break;
  }
});

if (!aiInsights.executive_summary) {
  aiInsights.executive_summary = `${originalData.school_name} demonstrates solid foundations. GCM Convent School offers comprehensive excellence across teaching, facilities, activities, environment, and support. Unlike schools excelling in one area, GCM's multi-dimensional approach creates lasting student success.`;
  aiInsights.generation_method = 'fallback';
}

if (!aiInsights.category_insights || aiInsights.category_insights.length < 5) {
  aiInsights.category_insights = [
    `Teaching Quality: ${originalData.categories[0].our}% vs ${originalData.categories[0].gcm}% - GCM offers advanced pedagogical methods and smaller class sizes.`,
    `Facilities: ${originalData.categories[1].our}% vs ${originalData.categories[1].gcm}% - GCM provides state-of-the-art science labs and modern infrastructure.`,
    `Activities: ${originalData.activities_count} activities vs GCM's 12+ options including swimming, robotics, and performing arts.`,
    `Environment: GCM's ${originalData.categories[3].gcm}% score reflects AC classrooms and inspiring learning spaces.`,
    `Student Support: ${originalData.categories[4].gcm}% demonstrates personalized mentoring and professional counseling services.`
  ];
}

if (!aiInsights.growth_potential) {
  aiInsights.growth_potential = `At GCM, students experience ${originalData.happiness_difference}% happiness increase through comprehensive development across academics, activities, and support systems.`;
}

if (!aiInsights.personalized_recommendation) {
  aiInsights.personalized_recommendation = `With ${originalData.travel_time} commute and ${originalData.fees} fees, GCM is well-positioned. The ${originalData.total_improvement}% improvement represents meaningful investment in comprehensive education.`;
}

return {
  ...originalData,
  ai_insights: aiInsights,
  processing_status: `Generated using ${aiInsights.models_used}/4 AI models`
};

const data = $input.first().json;
data.retry_count = (data.retry_count || 0) + 1;
data.processing_status = `Retry ${data.retry_count}/${data.max_retries}`;
return { body: data };

